pragma solidity ^0.4.24;

contract NewBet {

    enum BetStatus { OPEN, CLOSED }
    enum BetResult { OWNERWIN, CHALLENGERWIN, DRAW, PENDING, UNKNOWN }
    enum Operator { LESSTHAN, LESSTHANOREQUALTO, EQUALTO, MORETHANOREQUALTO, MORETHAN }

    address public owner;
    bool public ownerPosition;
    address public challenger;
    bool public challengerPosition;
    uint betId;
    string gameId;
    string Player;
    string statistic;
    uint Amount;
    Operator operator;
    BetStatus betStatus;
    BetResult betResult;
    uint Pot;
    uint Wager;


    constructor (string player, string _statistic, uint amount, uint _operator, bool position)
        public 
        payable {

            Player = player;
            statistic = _statistic;
            Amount = amount;
            operator = Operator(_operator);
            ownerPosition = position;
            owner = tx.origin;

            betStatus = BetStatus.OPEN;

            betResult = BetResult.PENDING;

            Wager = msg.value;

            }

    function takeBet(bool position)
        public
        payable {

            require(position != ownerPosition);
            require(msg.value >= Wager);

            Pot = Wager + msg.value;
            betStatus = BetStatus.CLOSED;
            challenger = msg.sender;
            challengerPosition = position;

        }

    function payOutChallenger(address challenger, uint Pot) public payable returns(bool) {

       // require (msg.sender == oracleAddress);
        challenger.transfer(Pot);
        betResult = BetResult.CHALLENGERWIN;
        }

    function payOutOwner(address owner, uint Pot) public payable returns(bool) {

       // require (msg.sender == oracleAddress);
        owner.transfer(Pot);
        betResult = BetResult.OWNERWIN;
        }

}


/////////////////////////////

contract BettingFactory {

  // index of created contracts

  address[] public contracts;

  // useful to know the row count in contracts index

  function getContractCount() 
    public 
    constant
    returns(uint contractCount) {
    return contracts.length;
    }


  // deploy a new betting contract

  function makeBet(string player, string _statistic, uint amount, uint _operator, bool position)
    public 
    payable 
    returns(address newContract) {

    NewBet b = (new NewBet).value(10)(player, _statistic, amount, _operator, position);
    contracts.push(b);
    return b;

    }
}
